import React,{Component} from 'react';
import './App.css';
import $ from 'jquery';
import ListDisplay from './ListDisplay'


class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      list:["Domain"],
      rows:[]
    }
    this.apicall(this);
    this.triggerapp = this.triggerapp.bind(this);
    this.domainapicall = this.domainapicall.bind(this);
    this.handleclick = this.handleclick.bind(this);
  }
  triggerapp(display_name){
    this.setState({
      list:[...this.state.list,display_name],
      rows:[]
    })
    this.domainapicall(display_name)
  }
  async domainapicall(domain){
    console.log(this.state.rows);
    const urlstring = `http://guru.southindia.cloudapp.azure.com:8001/knowlake/${domain.toLowerCase()}/list_entity_types?`;
    $.ajax({
      url:urlstring,
      success: (searchResults) => {
        console.log("Fetched data successfully")
        //console.log(searchResults)

        const results = searchResults
        //console.log(searchResults)
        const domains = [];
        results.forEach(element => {
          const entity = <ListDisplay key = {element.id} element = {element} trigger={this.triggerapp}/>
          domains.push(entity)
        });
         this.setState({
           rows:domains,
         })

      },
      error: (xhr, status, err) => {
        console.error("Failed to fetch data")
      }
    })
  }
  apicall(){
    const urlstring = "http://guru.southindia.cloudapp.azure.com:8001/knowlake/list_domains/";
    $.ajax({
      url:urlstring,
      success: (searchResults) => {
        console.log("Fetched data successfully")
        //console.log(searchResults)

        const results = searchResults
        //console.log(searchResults)
        const entities = [];
        results.forEach(element => {
          console.log(element)
          const entity = <ListDisplay key = {element.id} element = {element} trigger={this.triggerapp}/>
          entities.push(entity)
        });
         this.setState({
           rows:entities,
         })

      },
      error: (xhr, status, err) => {
        console.error("Failed to fetch data")
      }
    })
  }
  handleclick(e){
    console.log(`i am ${e}`);
    if(e === "Domain"){
      const list = this.state.list.pop();
      // this.setState({
      //   list:list,
      //   rows:[]
      // })
      // this.apicall();
      console.log(list);
    }else{
      console.log("do nothing");
    }
  }
  render(){
    return(
      <div className="App">
      <table className="titleBar">
          <tbody>
            <tr>

              <td width="8"/>
              <td>
                <h1>entity ingestion</h1>
              </td>
            </tr>
          </tbody>
        </table>
        <ul className ="list">
          {this.state.list.map(number => <li key={number}><button onClick ={()=> this.handleclick(number)}>{number}</button></li>)}
        </ul>
        <input style={{
          fontSize: 24,
          display: 'block',
          width: "30%",
          paddingTop: 8,
          paddingBottom: 8,
          paddingLeft: 16
        }}  placeholder="search here"/>
        {this.state.rows}
    </div>
    );
  }
}

export default App;
