import React,{Component} from 'react';
import './App.css';
import $ from 'jquery';
import ListDisplay from './ListDisplay'


class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      list:["Domain"],
      rows:[],
      inputfield:'',
    }
    const inputText = React.createRef();
    const domain  = "";
    this.apicall(this);
    this.triggerapp = this.triggerapp.bind(this);
    this.domainapicall = this.domainapicall.bind(this);
    this.handleclick = this.handleclick.bind(this);
  }
  triggerapp(display_name){
    this.setState({
      list:[...this.state.list,display_name],
      rows:[]
    })
    this.domain = display_name;
    this.domainapicall(display_name,"")
  }
  async domainapicall(domain,searchterm){
    console.log(this.state.rows);
    const urlstring = `http://guru.southindia.cloudapp.azure.com:8001/knowlake/${domain.toLowerCase()}/list_entity_types?q=${searchterm}`;
    $.ajax({
      url:urlstring,
      success: (searchResults) => {
        console.log("Fetched data successfully")
        //console.log(searchResults)

        const results = searchResults
        //console.log(searchResults)
        const domains = [];
        results.forEach(element => {
          const entity = <ListDisplay key = {element.id} element = {element} trigger={this.triggerapp}/>
          domains.push(entity)
        });
         this.setState({
           rows:domains,
         })

      },
      error: (xhr, status, err) => {
        console.error("Failed to fetch data")
      }
    })
  }
  apicall(){
    // this.setState({
    //   inputfield:'',
    // })
    const urlstring = "http://guru.southindia.cloudapp.azure.com:8001/knowlake/list_domains/";
    $.ajax({
      url:urlstring,
      success: (searchResults) => {
        console.log("Fetched data successfully")
        //console.log(searchResults)

        const results = searchResults
        //console.log(searchResults)
        const entities = [];
        results.forEach(element => {
          console.log(element)
          const entity = <ListDisplay key = {element.id} element = {element} trigger={this.triggerapp}/>
          entities.push(entity)
        });
         this.setState({
           rows:entities,
         })

      },
      error: (xhr, status, err) => {
        console.error("Failed to fetch data")
      }
    })
  }
  handleclick(e){
    console.log(`i am ${e}`);
    if(this.state.list.length>1){
      const list = this.state.list.pop();
    }
    if(e === "Domain"){

      this.setState({
        list:this.state.list,
        rows:[]
      })
      this.apicall();

    }else{
      console.log("do nothing");
    }
  }

  searchChangeHandler(event) {
    console.log(event.target.value)
    const boundObject = this
    const searchTerm = event.target.value
    boundObject.domainapicall(this.domain,searchTerm)
    //this.refs.Input.value = "";
  }

  render(){
    return(
      <div className="App">
      <table className="titleBar">
          <tbody>
            <tr>

              <td width="8"/>
              <td>
                <h1>entity ingestion</h1>
              </td>
            </tr>
          </tbody>
        </table>
        <ul className ="list">
          {this.state.list.map(number => <li key={number}><button onClick ={()=> this.handleclick(number)}>{number}</button></li>)}
        </ul>
        <input ref={node =>this.inputText = node} style={{
          fontSize: 24,
          display: 'block',
          width: "30%",
          paddingTop: 8,
          paddingBottom: 8,
          paddingLeft: 16
        }}onChange={this.searchChangeHandler.bind(this)}  placeholder="search here"/>
        {this.state.rows}
    </div>
    );
  }
}

export default App;
